import { useEffect, useState } from "react";
import { db } from "../config/firebase-config";
import {
  query,
  collection,
  where,
  onSnapshot,
  orderBy,
} from "firebase/firestore";
import { useGetUserInfo } from "./useGetUserInfo";

export const useGetTransaction = () => {
  // const [transactions, setTransactions] = useState([]);
  let allTransactins = [];
  const [transactionToals, setTransactionTotals] = useState({
    balance: 0.0,
    income: 0.0,
    expense: 0.0,
  });
  const { userID } = useGetUserInfo("auth");

  const getTransactions = async () => {
    const q = query(
      collection(db, "transactions"),
      where("userID", "==", userID),
      orderBy("createAt")
    );

    const unsubscribe = onSnapshot(q, (querySnapshot) => {
      let totalIncome = 0;
      let totalExpense = 0;

      querySnapshot.forEach((doc) => {
        allTransactins.push(doc.data());

        if (doc.data().transactionType === "expense") {
          totalExpense += Number(doc.data().transactionAmount);
        } else {
          totalIncome += Number(doc.data().transactionAmount);
        }
      });

      // setTransactions(allTransactins);

      console.log("tansaction" + allTransactins.length);

      let balance = totalIncome - totalExpense;

      setTransactionTotals({
        balance,
        income: totalIncome,
        expense: totalExpense,
      });
    });

    return allTransactins;
  };

  useEffect(() => {
    getTransactions();
  }, []);

  return { allTransactins, transactionToals };
};
